import React from 'react';
import PropTypes from 'prop-types';

class ProductInfo extends React.Component {
  state = {
    cartProducts: [],
    product: {},
    checkbox0: false,
    checkbox1: false,
    checkbox2: false,
    checkbox3: false,
    checkbox4: false,
    showReview: false,
    reviewheckbox0: false,
    reviewheckbox1: false,
    reviewheckbox2: false,
    reviewheckbox3: false,
    reviewheckbox4: false,
    email: '',
    obs: '',
    totalCart:0

  }

  componentDidMount = () => {
    const { location: { state: infos }, history } = this.props;

    let getFavorites = JSON.parse(localStorage.getItem('productCart'));
    const ajusteFav = getFavorites || [];


    const getReview = JSON.parse(localStorage.getItem('review')) || [];

    const ajustReviewInProduct = getReview.find((el) => {
      if (el.id === infos.id) {
        infos.review = el;
        return true;
      }
    });

    const verificaQtd = ajusteFav.find((el) => {
      if (el.id === infos.id) {
        if (el.tamanho === undefined) {
          el.tamanho = 0;
          infos.tamanho = 0;
        }
        infos.tamanho = el.tamanho;
        return true;
      }
    });

    if (verificaQtd === undefined) {
      infos.tamanho = 0;
    }

    getFavorites = ajusteFav;
    this.setState({ cartProducts: getFavorites, product: infos }, () => {
      const { product } = this.state;

      if (product.review) {
        this.setState({ showReview: true });
      }
    });
    const total = ajusteFav.reduce((ant,at)=>ant+at.tamanho,0)
    this.setState({totalCart:total})
  }

  addToCart = (op) => {
    const { product } = this.state;
    const getFavorites = JSON.parse(localStorage.getItem('productCart'));
    const ajusteFav = getFavorites || [];
    let ajustedCart = ajusteFav.filter((el) => el.id !== product.id);
    // console.log(product)
    if (product.tamanho < 2 && op === -1) {
      product.tamanho = 1;
    } else {
      product.tamanho = (product.tamanho) ? product.tamanho += op : 1;
    }

    ajustedCart = [...ajustedCart, product];

    this.setState({ cartProducts: ajustedCart }, () => {
      const { cartProducts: Carrinho } = this.state;
      localStorage.setItem('productCart', JSON.stringify(Carrinho));
      const total = Carrinho.reduce((ant,at)=>ant+at.tamanho,0)
      this.setState({totalCart:total})
    });
  }

  validCheckbox = (index) => {
    console.log(index < 2);
    this.setState({
      checkbox0: index >= 0,
      checkbox1: index >= 1,
      checkbox2: index >= 2,
      checkbox3: index >= 3,
      checkbox4: index >= 4,
    });
  }

  handleInput = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  }

  handlesubmit = (e) => {
    const { cartProducts, product, checkbox0, checkbox1, checkbox2, checkbox3, checkbox4, email, obs } = this.state;

    let getReview = JSON.parse(localStorage.getItem('review')) || [];

    getReview = [...getReview, { id: product.id, checkbox0, checkbox1, checkbox2, checkbox3, checkbox4, email, obs }];

    const a = [...cartProducts];
    a.forEach((el) => {
      if (el.id === product.id) {
        el.review = { product, checkbox0, checkbox1, checkbox2, checkbox3, checkbox4, email, obs };
        // product.review = { product, checkbox0, checkbox1, checkbox2, checkbox3, checkbox4, email, obs };
      }
    });
    // console.log(cartProducts)
    e.preventDefault();
    product.review = { product, checkbox0, checkbox1, checkbox2, checkbox3, checkbox4, email, obs };
    console.log(product);
    this.setState(() => this.setState({ product, email: '', obs: '' }), () => this.setState({ showReview: true }));

    localStorage.setItem('review', JSON.stringify(getReview));
  }

  render() {
    const { location: { state: infos }, history } = this.props;
    const { product, checkbox0, checkbox1, checkbox2, checkbox3, checkbox4, showReview, email, obs } = this.state;
    const { totalCart } = this.state;
    // console.log(product)
    const { checkbox0: reviewheckbox0, checkbox1: reviewheckbox1, checkbox2: reviewheckbox2, checkbox3: reviewheckbox3, checkbox4: reviewheckbox4, email: emailReview, obs: obsReview } = product.review || product;

    return (
      <div>
        <form onSubmit={ this.handlesubmit }>
          <p data-testid="product-detail-name">{product.title}</p>
          <input
            data-testid="product-detail-email"
            name="email"
            type="email"
            value={ email }
            onChange={ (e) => this.handleInput(e) }
          />

          <input
            data-testid="1-rating"
            checked={ checkbox0 }
            onChange={ () => this.validCheckbox(0) }
            type="checkbox"
          />
          <input
            data-testid="2-rating"
            checked={ checkbox1 }
            onChange={ () => this.validCheckbox(1) }
            type="checkbox"
          />
          <input
            data-testid="3-rating"
            checked={ checkbox2 }
            onChange={ () => this.validCheckbox(2) }
            type="checkbox"
          />
          <input
            data-testid="4-rating"
            checked={ checkbox3 }
            onChange={ () => this.validCheckbox(3) }
            type="checkbox"
          />
          <input
            data-testid="5-rating"
            checked={ checkbox4 }
            onChange={ () => this.validCheckbox(4) }
            type="checkbox"
          />
          <button type="submit" data-testid="submit-review-btn">Enviar Avaliação</button>
          <textarea
            name="obs"
            value={ obs }
            data-testid="product-detail-evaluation"
            onChange={ (e) => this.handleInput(e) }
          />

          <button
            type="button"
            data-testid="product-detail-add-to-cart"
            onClick={ () => this.addToCart(infos, 1) }
          >
            Adicionar ao carrinho
          </button>

          <button
            onClick={ () => history.push('/cart') }
            type="button"
            data-testid="shopping-cart-button"
          >
            Ir para o Carrinho

          </button>

          <p>Quantidade</p>
          <button
            type="button"
            data-testid="product-increase-quantity"
            onClick={ () => this.addToCart(1) }
          >
            +
          </button>
          <p data-testid="product-detail-quantidade">{product.tamanho}</p>
          <button
            type="button"
            data-testid="product-decrease-quantity"
            onClick={ () => this.addToCart(-1) }
          >
            -
          </button>

        </form>

        <h2 data-testid="shopping-cart-size">{totalCart}</h2>

        {showReview && (
          <div>
            <p>{emailReview}</p>
            <p>{obsReview}</p>
            <input
              disabled
              data-testid="review-1-rating"
              checked={ reviewheckbox0 }
              onChange={ () => this.validCheckbox(0) }
              type="checkbox"
            />
            <input
              disabled
              data-testid="review-2-rating"
              checked={ reviewheckbox1 }
              onChange={ () => this.validCheckbox(1) }
              type="checkbox"
            />
            <input
              disabled
              data-testid="review-3-rating"
              checked={ reviewheckbox2 }
              onChange={ () => this.validCheckbox(2) }
              type="checkbox"
            />
            <input
              disabled
              data-testid="review-4-rating"
              checked={ reviewheckbox3 }
              onChange={ () => this.validCheckbox(3) }
              type="checkbox"
            />
            <input
              disabled
              data-testid="review-5-rating"
              checked={ reviewheckbox4 }
              onChange={ () => this.validCheckbox(4) }
              type="checkbox"
            />
          </div>
        )}
      </div>
    );
  }
}
ProductInfo.propTypes = {
  history: PropTypes.instanceOf(Object).isRequired,
  location: PropTypes.instanceOf(Object).isRequired,

};

export default ProductInfo;
