import React from 'react';

class ShoppingCart extends React.Component {
  constructor() {
    super();
    this.state = {
      listCart: [],
      arrTamanho:[]
    };
  }

  componentDidMount = () => {
    const getFavorites = JSON.parse(localStorage.getItem('productCart'));
    const ajusteFav = getFavorites || [];
    this.setState({ listCart: ajusteFav });
  }

  ajustNewProduct= (fav, op) => {
    const { listCart } = this.state;

    const arrTamanho = listCart.filter((el) => el.id !== fav.id);
    console.log(arrTamanho)
    if (fav.tamanho < 2 && op === -1) {
      fav.tamanho = 0;
    } else {
      fav.tamanho += op;
    }

    this.setState({ arrTamanho }, () => {
      const { listCart: Carrinho } = this.state;
      localStorage.setItem('productCart', JSON.stringify(Carrinho));
    });
  }

  render() {
    const { listCart } = this.state;
    const validListCart = (listCart.length === 0);
    return (
      <div>
        {validListCart && (
          <p data-testid="shopping-cart-empty-message">
            Seu carrinho est√° vazio
          </p>)}
        <div>
          {listCart.map((fav) => (
            <div key={ fav.id }>
              <p data-testid="shopping-cart-product-name">{fav.title}</p>
              <p data-testid="shopping-cart-product-quantity">{fav.tamanho}</p>
              <button
                type="button"
                data-testid="product-increase-quantity"
                onClick={ () => this.ajustNewProduct(fav, 1) }
              >
                +
              </button>
              <button
                type="button"
                data-testid="product-decrease-quantity"
                onClick={ () => this.ajustNewProduct(fav, -1) }
              >
                -
              </button>

            </div>
          ))}
        </div>
      </div>
    );
  }
}
export default ShoppingCart;
