import React from 'react';
import PropTypes from 'prop-types';

class ProductInfo extends React.Component {
  state = {
    cartProducts: [],
    checkbox0:false, 
    checkbox1:false,
    checkbox2:false, 
    checkbox3:false, 
    checkbox4:false
  }

  componentDidMount = () => {
    const { location: { state: infos }, history } = this.props;

    let getFavorites = JSON.parse(localStorage.getItem('productCart'));
    const ajusteFav = getFavorites || [];

    const verificaQtd = ajusteFav.find((el) => {
      if (el.id === infos.id) {
        if (el.tamanho === undefined) {
          el.tamanho = 0;
          infos.tamanho = 0;
        }
        infos.tamanho = el.tamanho;
        return true;
      }
    });

    if (verificaQtd === undefined) {
      infos.tamanho = 0;
    }

    getFavorites = ajusteFav;
    this.setState({ cartProducts: getFavorites, product: infos });
  }

  addToCart = (op) => {
    const { product } = this.state;
    const getFavorites = JSON.parse(localStorage.getItem('productCart'));
    const ajusteFav = getFavorites || [];
    let ajustedCart = ajusteFav.filter((el) => el.id !== product.id);
    // console.log(product)
    if (product.tamanho < 2 && op === -1) {
      product.tamanho = 1;
    } else {
      product.tamanho = (product.tamanho) ? product.tamanho += op : 1;
    }

    ajustedCart = [...ajustedCart, product];

    this.setState({ cartProducts: ajustedCart }, () => {
      const { cartProducts: Carrinho } = this.state;
      localStorage.setItem('productCart', JSON.stringify(Carrinho));
    });
  }

  validCheckbox = () => {

  }

  render() {
    const { location: { state: infos }, history } = this.props;
    const {checkbox0, checkbox1,checkbox2, checkbox3, checkbox4} = this.state


    return (
      <div>
        <form>
          <p data-testid="product-detail-name">{infos.title}</p>
          <input
          data-testid="product-detail-email"
          type='email'
          />

          <input
          data-testid="0-rating"
          value={checkbox0}
          onChange={()=>this.validCheckbox(0)}
          type='checkbox'
          />
          <input
          data-testid="1-rating"
          value={checkbox1}
          onChange={()=>this.validCheckbox(1)}
          type='checkbox'
          />
          <input
          data-testid="2-rating"
          value={checkbox2}
          onChange={()=>this.validCheckbox(2)}
          type='checkbox'
          />
          <input
          data-testid="3-rating"
          value={checkbox3}
          onChange={()=>this.validCheckbox(3)}
          type='checkbox'
          />
          <input
          data-testid="4-rating"
          value={checkbox4}
          onChange={()=>this.validCheckbox(4)}
          type='checkbox'
          />

          <button
            type="button"
            data-testid="product-detail-add-to-cart"
            onClick={ () => this.addToCart(infos, 1) }
          >
            Adicionar ao carrinho
          </button>

          <button
            onClick={ () => history.push('/cart') }
            type="button"
            data-testid="shopping-cart-button"
          >
            Ir para o Carrinho

          </button>

          <p>Quantidade</p>
          <button
            type="button"
            data-testid="product-increase-quantity"
            onClick={ () => this.addToCart(1) }
          >
            +
          </button>
          <p data-testid="product-detail-quantidade">{infos.tamanho}</p>
          <button
            type="button"
            data-testid="product-decrease-quantity"
            onClick={ () => this.addToCart(-1) }
          >
            -
          </button>

        </form>
      </div>
    );
  }
}
ProductInfo.propTypes = {
  history: PropTypes.instanceOf(Object).isRequired,
  location: PropTypes.instanceOf(Object).isRequired,

};

export default ProductInfo;
