import { render } from "@testing-library/react";
import React from "react";

class ModalCart extends React.Component{
    render(){
        const {listCategories, selectedRadio, handleChangeRadio, showCategories} = this.props
        return(
           <aside>
               {listCategories.map((el) => (
                <div>
            <label key={ el.id } data-testid="category" htmlFor={ el.id }>

              <input
                id={ el.id }
                name="category"
                onChange={ (e) => handleChangeRadio(e,el) }
                type="radio"
                value={ el.name === selectedRadio }
              />
              {el.name}
            </label>
            <button type="click"onClick={showCategories}>x</button>
                </div>

          ))}
           </aside> 
        )
    }
}

export default ModalCart;
